import java.util.*;
public class SumOfFirst50NaturalNumberes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 50;
		int sum = 0;
		for(int i = 1 ; i<= n ; i++) {
			sum = sum + i;
		}
		System.out.println("The Sum of First 50 Natural Number is "+sum);

	}

}
